import { REMOVE_FAV, ADD_FAV } from "./actions";

const initialState = {
    myFavorites: [],
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAV:
      return { ...state, myFavorites: action.payload, allCharacters: action.payload };
      case REMOVE_FAV:
        return { ...state, myFavorites: action.payload, allCharacters: action.payload };
                default:
                    return {
                        ...state,
                    };
    }
};

export default reducer;
